import Foundation


class Ahorcado: ObservableObject {
    
    @Published var strikes = 0
    @Published var imagenStrikes = ""
    @Published var palabra:NSString = ""
    @Published var jugando = false
    @Published var cantidadLetras = 0
    @Published var letras = ""
    @Published var mensaje = ""
    @Published var lineasYLetras = [Character]()
    @Published var gameOver = false
    @Published var intentoPalabra: NSString = ""
    @Published var palabraAcertada = false
    @Published var nivelDificultad = ["Difícil","Medio","Fácil","Muy Fácil"] //vocales: 1,2,3,4
    @Published var cuentaVocales = 0
    @Published var dificultadElegida = 1
var sinVocales = false
    
    var tienesVocales = true
    
    var palabraArray = [Character]()
    var turnoLetra = ""
    var pidoVocal = false
   
    var conVocales = true
    
    
    func empezar() {
        palabra = diccionario[Int.random(in: 0..<diccionario.count-1)] as NSString
        lineasYLetras = []
        palabraArray = []
        strikes = 0
        imagenStrikes = "Ahorcado_0"
        cuentaVocales = 0
        conVocales = true
        pidoVocal = false
        sinVocales = false
        jugando.toggle()
        intentoPalabra = ""
       
        
        for l in palabra as String {
            
            palabraArray.append(l)
            lineasYLetras.append("➖")
        }
        jugando = true
        gameOver = false
    }
    
    //MARK: Func pidoLetra
    func pidoLetra(letraR: Character) {
        let letra: Character = Character(letraR.uppercased())
        
        tienesVocales = dificultadElegida == 0 ? false : true
        
        if vocales.contains(letra) {
            tienesVocales = cuentaVocales >= dificultadElegida ? false : true
            if tienesVocales {
                cuentaVocales += 1
                tienesVocales = cuentaVocales >= dificultadElegida ? false : true
                print("\(cuentaVocales) - \(dificultadElegida) Tienes Vocales? : \(tienesVocales ? "T SI" : "F NO")")
                if palabraArray.contains(letra) {
                 
                    palabraSiContieneLetra(letra: letra)
                   print("\(letra): Intento Correcto- sustituida por Letra \n")
                   
                } else {
                    print("\(letra): Con vocales, pero fallido por letra no encontrada \n")
                    intentoFallido()
                    
                }
                
            } else {
                print("\(letra): Intento Fallido por no tener vocales \n")
                intentoFallido()
            }
            
            
        } else if palabraArray.contains(letra) {
            print("\(letra): Intento correcto con Consonante \n")
            
            palabraSiContieneLetra(letra: letra)
            
        } else {
            print("\(letra): Intento incorrecto con Consonante \n")
            print("Game Over \(gameOver ? "T": " F ")")
            intentoFallido()
        }
    }
    
    func pidoPalabra(conR: String) {
        let con = conR.uppercased()
        
        if con == palabra as String {
            palabraAcertada = true
            gameOver = true
            jugando = false
        } else {
            strikes += 1
            switch strikes {
            case 0 : imagenStrikes = "NewGame"
            case 1: imagenStrikes = "Ahorcado_1"
            case 2: imagenStrikes = "Ahorcado_2"
            case 3: imagenStrikes = "Ahorcado_3"
            case 4: imagenStrikes = "Ahorcado_4"
            case 5: imagenStrikes = "Ahorcado_5"
            case 6: imagenStrikes = "Ahorcado_6" ; gameOver = true
            default : imagenStrikes = "Ahorcado_0"
        }
    }
     intentoPalabra = ""
    }
    
    func ultimoIntento() {
        
        intentoPalabra = ""
        
    }
    
    func palabraSiContieneLetra(letra: Character) {
        
        var encontrada = true
        repeat {
            if let i = palabraArray.firstIndex(of: letra) {
                lineasYLetras[i] = letra
                palabraArray[i] = "➖"
            } else {
                encontrada = false
            }
            
            } while encontrada == true
        }
        
    func intentoFallido() {
        strikes += 1
        
        switch strikes {
        case 0 : imagenStrikes = "NewGame"
        case 1: imagenStrikes = "Ahorcado_1"
        case 2: imagenStrikes = "Ahorcado_2"
        case 3: imagenStrikes = "Ahorcado_3"
        case 4: imagenStrikes = "Ahorcado_4"
        case 5: imagenStrikes = "Ahorcado_5"
        case 6: imagenStrikes = "Ahorcado_6"
        default : imagenStrikes = "Ahorcado_0"
        
    }
        gameOver = strikes == 6 ? true  : false
        print("Strikes: \(strikes) - GO \(gameOver ? "T" : "F")")
        jugando = gameOver ? false : true
    }
    
